package Oca808;

public class Ques108 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int idx=0;
		int size =3;
		
		while(idx<size-1) {
			idx++;
			System.out.println(idx);
		}
		System.out.println(idx);
		
		idx=0;
		size =3;
		
		do {
			idx++;
			System.out.println(idx);
		}while(idx<size-1) ;
		System.out.println(idx);
		

	}

}
